SELECT a.id, a.title, a.click, a.date, a.address, a.view, a.duration, a.info, b.id category_id, b.name category_name, c.id author_id, c.username author_username, c.nickname author_nickname, c.head_img author_head_img FROM videotable a, categorytable b, usertable c WHERE a.title like CONCAT(CONCAT('%', #{video.title}), '%') AND a.category=b.id AND a.author=c.id
${optionDurationSql}
ORDER BY ${orderItem} DESC
